con:config.script:
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context);

def properties = testRunner.testCase.getTestStepByName("Properties");
properties.setPropertyValue("Language", "") 
properties.setPropertyValue("Option", "") 
properties.setPropertyValue("Text", "") 
properties.setPropertyValue("Errors", "") 
properties.setPropertyValue("Suggestions", "") 
properties.setPropertyValue("countErrors", "") 
properties.setPropertyValue("countSuggestions", "") 


def filePath = context.expand( '${#Project#dataFilePath}')
def langGlobal = context.expand( '${#Project#lang}')

if (new File(filePath).isAbsolute() == false ) {
	filePath = groovyUtils.projectPath + "//" + filePath;
}

log.info "Data file: " + filePath
context.fileReader = new BufferedReader(new FileReader(filePath))
rowsData = context.fileReader.readLines()

int rowsSize = rowsData.size() 
def fields
for (int i =1 ; i < rowsSize; i++) {
	fields = rowsData[i].split(";", -1)	
	lang = fields[0];
	if( !lang.equals(langGlobal)) { continue }
	log.info rowsData[i]
	properties.setPropertyValue("Language", fields[0]) 		
	properties.setPropertyValue("Option", fields[1])  
	properties.setPropertyValue("Text", fields[2])  
	properties.setPropertyValue("Errors", fields[3])  
	properties.setPropertyValue("Suggestions", fields[4])
	properties.setPropertyValue("countErrors", countWords(fields[3]))  
	properties.setPropertyValue("countSuggestions", countWords(fields[4]))	
	testRunner.runTestStepByName("SOAP Request checkText")
}

def countWords(str) 
{
	if (str.trim().equals("")) {
		return "0"
	}
	return str.split(",").length.toString()	
}

con:configuration.scriptText:
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def responseHolder = groovyUtils.getXmlHolder( messageExchange.responseContent )
responseHolder.namespaces["ns"] = "http://speller.yandex.net/services/spellservice"
def wrongWordsActual = responseHolder.getNodeValues("//ns:word")
log.info "Actual wrong words = " + wrongWordsActual.toString()

def wrongWordsExpected = context.expand('${Properties#Errors}')
if (!wrongWordsExpected.equals("")) {
	wrongWordsExpected = wrongWordsExpected.split(",").collect{it.trim()}
}
log.info "Expected wrong words = " + wrongWordsExpected.toString()

wrongWordsExpected.each {
	log.info "Check that expected word =" + it + " in " + 	wrongWordsActual.toString();
	assert wrongWordsActual.contains(it)	
}
wrongWordsActual.each {
	log.info "Check that actual word =" + it + " in " + 	wrongWordsExpected.toString();
	assert wrongWordsExpected.contains(it)	
}

def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def responseHolder = groovyUtils.getXmlHolder( messageExchange.responseContent )
responseHolder.namespaces["ns"] = "http://speller.yandex.net/services/spellservice"
def suggestionWordsActual = responseHolder.getNodeValues("//ns:s")
log.info "Actual suggestion words = " + suggestionWordsActual.toString()

def suggestionWordsExpected = context.expand('${Properties#Suggestions}')
if (!suggestionWordsExpected.equals("")) {
	suggestionWordsExpected = suggestionWordsExpected.split(",").collect{it.trim()}
}
log.info "Expected suggestion words = " + suggestionWordsExpected.toString()

if (suggestionWordsExpected.size() > 0) {
	suggestionWordsExpected.each {
		log.info "Check that expected suggestion =" + it + " in " + suggestionWordsActual.toString();
		assert suggestionWordsActual.contains(it)	
	}
}

suggestionWordsActual.each {
	log.info "Check that actual suggestion =" + it + " in " + suggestionWordsExpected.toString();
	assert suggestionWordsExpected.contains(it)	
}

con:testSuite.con:setupScript:
def langGlobal
if  (testSuite.getPropertyValue("useSuiteLang") == "1") {
	langGlobal = testSuite.getPropertyValue("lang")
} else {
	langGlobal = context.expand( '${#Project#lang}')	
}


def textVarName = getTextVarName(langGlobal)

if (textVarName.isEmpty()) {
	testSuite.setPropertyValue("lang", "Error! not recognized for this suit")	
	disableTestCases() 
} else {
	testSuite.setPropertyValue("lang", langGlobal)
	prepareTestCases(textVarName) 
}

def disableTestCases() {
	testSuite.getTestCaseList().each {
		it.disabled = true
		log.info "Setup (desable to prevent run) testCase " + it.name
	}
}

def prepareTestCases(textVarName) {
	testSuite.getTestCaseList().each {
		it.disabled = false
		def text = it.getPropertyValue(textVarName)	
		it.setPropertyValue("text", text)
		log.info "New text value: ' " + text + " ' for  " + it.name
	}
}	

def getTextVarName(langGlobal) {	
	switch (langGlobal) {
		case "ru": 
			textVarName = "textRU"			
			break
		case "en": textVarName = "textEN"
		break
		case "uk": textVarName = "textUK"
		break	
		default: textVarName = ""
		break
	}	
	return textVarName
}

con:testSuite.con:tearDownScript:
testSuite.getTestCaseList().each {
	if (it.disabled) {		
		it.disabled = false
		log.info "Teardown (enable testcase): " + it.name
	}
}
